import React, { useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const ForgotPasswordPage = () => {
  const [email, setEmail] = useState("");
  const [message, setMessage] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post("http://localhost:5000/api/users/forgot-password", { email });
      setMessage("üì© Un email de r√©initialisation a √©t√© envoy√© !");
    } catch (error) {
      setMessage("‚ùå Probl√®me serveur ou email incorrect.");
    }
  };

  return (
    <div style={{
        //backgroundColor: "red",
        backgroundImage: `url('/assets/marvelstorepasword.jpg')`, // ‚úÖ V√©rifie bien ce chemin
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
      <h2 className="text-2xl font-bold mb-4">Mot de passe oubli√©</h2>
      
      {message && <p className="text-yellow-400">{message}</p>}

      <form onSubmit={handleSubmit} className="flex flex-col">
        <label className="mb-2">Email</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="p-3 mb-4 rounded text-black"
          required
        />

        <button type="submit" className="bg-blue-600 hover:bg-blue-700 p-3 rounded font-bold">
          Envoyer un lien de r√©initialisation
        </button>
      </form>

      <Link to="/login" className="mt-4 text-white hover:underline">
        Retour √† la connexion
      </Link>
    </div>
  );
};

export default ForgotPasswordPage;
