import { useNavigate, Link, useLocation } from 'react-router-dom';
import { Icon } from '@iconify/react';
import { useState, useEffect } from 'react';

function HomePage() {
  const navigate = useNavigate();
  const location = useLocation();
  const [user, setUser] = useState(null);

  // Charger l'utilisateur depuis localStorage au montage
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const handleViewProducts = () => {
    console.log("Redirection vers la page des produits");
    navigate('/products');
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    setUser(null);
    navigate('/login');
  };

  return (
    <div style={{
      backgroundImage: `url('/assets/homepage.webp')`,
      backgroundSize: "cover",
      backgroundPosition: "center",
      backgroundRepeat: "no-repeat",
      minHeight: "100vh",
      display: "flex",
      flex: "column",
      alignItems: "center",
      justifyContent: "center",
      textAlign: "center",
      padding: "20px",
      color: "#fff"
    }}>
      <h1>Bienvenue sur Marvel Store</h1>
      <p>Ajoutez vos produits et profitez du shopping en ligne !</p>
      <button onClick={handleViewProducts}style={{ marginTop: "15px", backgroundColor: "green", color: "white", padding: "10px", borderRadius: "5px", width: "80%" }}>Entrer dans la boutique</button>
      <p>Description:</p>
      <p>Bienvenue sur la meilleure application web de vente en ligne...</p>
      <p>FAITES-VOUS PLAISIR ! ! !</p>
      
      {/* Barre sup√©rieure + Navigation */}
      <header style={{ display: "flex", flexDirection: "column", backgroundColor: "#222", color: "white", padding: "10px 1px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          <Link to="/" className="nav-link">
            <Icon icon="mdi:home" width="32" color={location.pathname === "/" ? "red" : "white"} />
          </Link>
          <Link to="/products" className="nav-link">
            <Icon icon="mdi:shopping" width="32" color={location.pathname === "/products" ? "red" : "white"} />
          </Link>
          <Link to="/cart" className="nav-link">
            <Icon icon="mdi:cart" width="32" color={location.pathname === "/cart" ? "red" : "white"} />
          </Link>
          <Link to="/account" className="nav-link">
            <Icon icon="mdi:account" width="32" color={location.pathname === "/account" ? "red" : "white"} />
          </Link>
          <Link to="/wallet" className="nav-link">
            <Icon icon="mdi:credit-card" width="32" color={location.pathname === "/wallet" ? "red" : "white"} />
          </Link>
          <Link to="/addresses" className="nav-link">
            <Icon icon="mdi:map-marker" width="32" color={location.pathname === "/addresses" ? "red" : "white"} />
          </Link>
        </nav>
        
        {/* Barre utilisateur */}
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "0 20px" }}>
          <h1>üõçÔ∏è La meilleure Boutique</h1>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Icon icon="mdi:account-circle" width="40" style={{ cursor: "pointer" }} />
            {user ? (
              <>
                <span style={{ marginLeft: "10px", fontSize: "16px" }}>{user.name}</span>
                <button onClick={handleLogout} style={{ marginLeft: "10px", backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px" }}>D√©connexion</button>
              </>
            ) : (
              <button onClick={() => navigate("/login")} style={{ marginLeft: "10px", backgroundColor: "blue", color: "white", padding: "5px", borderRadius: "3px" }}>Login</button>
            )}
          </div>
        </div>
      </header>
    </div>
  );
}

export default HomePage;
