import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { Icon } from "@iconify/react";
import axios from "axios";

const CartPage = () => {
  const [cart, setCart] = useState(() => JSON.parse(localStorage.getItem("cart")) || []);
  const [user, setUser] = useState(() => JSON.parse(localStorage.getItem("user")) || null);
  const [address, setAddress] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("card");
  const [cardDetails, setCardDetails] = useState({ number: "", date: "", cvc: "" });
  const [paypalEmail, setPaypalEmail] = useState("");
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    localStorage.setItem("cart", JSON.stringify(cart));
  }, [cart]);

  const removeFromCart = (index) => {
    const updatedCart = cart.filter((_, i) => i !== index);
    setCart(updatedCart);
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const getDeliveryDate = () => {
    const today = new Date();
    today.setDate(today.getDate() + 2);
    return today.toLocaleDateString("fr-FR", { weekday: "long", day: "numeric", month: "long" });
  };

  const handlePayment = async () => {
    try {
      await axios.post("http://localhost:5000/api/updateStock", { cart });
      alert(`Paiement effectué avec succès ! Votre commande arrivera le ${getDeliveryDate()}`);
      setCart([]);
      localStorage.removeItem("cart");
    } catch (error) {
      console.error("Erreur lors du paiement :", error);
      alert("Une erreur est survenue lors du paiement. Veuillez réessayer.");
    }
  };

  return (
    <div>
      {/* Barre supérieure + Navigation */}
      <header style={{ display: "flex", flexDirection: "column", backgroundColor: "#222", color: "white", padding: "10px 1px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          {[
            { path: "/", icon: "mdi:home" },
            { path: "/products", icon: "mdi:shopping" },
            { path: "/cart", icon: "mdi:cart" },
            { path: "/account", icon: "mdi:account" },
            { path: "/wallet", icon: "mdi:credit-card" },
            { path: "/addresses", icon: "mdi:map-marker" },
          ].map((item) => (
            <Link key={item.path} to={item.path} className="nav-link">
              <Icon icon={item.icon} width="32" color={location.pathname === item.path ? "red" : "white"} />
            </Link>
          ))}
        </nav>
      

        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "0 20px" }}>
            <h1>🛍️ Passer à la caisse</h1>
            <div style={{ display: "flex", alignItems: "center" }}>
                <Icon icon="mdi:account-circle" width="40" style={{ cursor: "pointer" }} />
                {user ? (
                    <>
                    <span style={{ marginLeft: "10px", fontSize: "16px" }}>{user.name}</span>
                    <button onClick={handleLogout} style={{ marginLeft: "10px", backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px" }}>Déconnexion</button>
                    </>
                ) : (
                <button onClick={() => navigate("/login")} style={{ marginLeft: "10px", backgroundColor: "blue", color: "white", padding: "5px", borderRadius: "3px" }}>Login</button>
                )}
            </div>
        </div>
        </header>

      <div style={{ 
        backgroundImage: `url('/assets/marvelstorepanier.jpeg')`, // ✅ Chemin corrigé
        backgroundSize: "cover",
        marginTop: "120px", 
        flex: 1, 
        padding: "20px" }}>
      <main style={{ marginTop: "20px", padding: "20px" }}>
        <h2>🛒 Mon Panier</h2>

        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: "20px" }}>
          {cart.map((product, index) => (
            <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#222", color: "#fff" }}>
              <h3>{product.name}</h3>
              <p>Prix : {product.price}€</p>
              {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
              <button onClick={() => removeFromCart(index)} style={{ backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px", marginTop: "10px" }}>Supprimer</button>
            </div>
          ))}
        </div>

        {cart.length > 0 && (
          <div style={{ backgroundColor: "#222", padding: "20px", borderRadius: "10px", marginTop: "20px", color: "white" }}>
            <h3>💳 Paiement</h3>
            <label>Adresse de livraison:</label>
            <input type="text" value={address} onChange={(e) => setAddress(e.target.value)} placeholder="Votre adresse complète" style={{ width: "50%", padding: "5px", marginTop: "5px" }} />

            <label>Mode de paiement:</label>
            <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)} style={{ width: "10%", padding: "5px", marginTop: "5px" }}>
              <option value="card">Carte bancaire</option>
              <option value="paypal">PayPal</option>
            </select>

            {paymentMethod === "card" && (
              <>
                <input type="text" value={cardDetails.number} onChange={(e) => setCardDetails({ ...cardDetails, number: e.target.value })} placeholder="Numéro de carte" style={{ width: "25%", padding: "5px", marginTop: "5px" }} />
                <input type="text" value={cardDetails.date} onChange={(e) => setCardDetails({ ...cardDetails, date: e.target.value })} placeholder="Date d'expiration" style={{ width: "25%", padding: "5px", marginTop: "5px" }} />
                <input type="text" value={cardDetails.cvc} onChange={(e) => setCardDetails({ ...cardDetails, cvc: e.target.value })} placeholder="CVC" style={{ width: "25%", padding: "5px", marginTop: "5px" }} />
              </>
            )}

            {paymentMethod === "paypal" && (
              <input type="email" value={paypalEmail} onChange={(e) => setPaypalEmail(e.target.value)} placeholder="Email PayPal" style={{ width: "100%", padding: "5px", marginTop: "5px" }} />
            )}

            <p>📅 Date de livraison prévue: <strong>{getDeliveryDate()}</strong></p>

            <button onClick={handlePayment} style={{ backgroundColor: "green", color: "white", padding: "10px", borderRadius: "5px", marginTop: "10px", width: "100%" }}>Effectuer le paiement</button>
          </div>
        )}
      </main>
      </div>
    </div>
  );
};

export default CartPage;
