import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Icon } from "@iconify/react";
import axios from "axios";

const RegisterPage = () => {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [number, setNumber] = useState(""); 
  const [recovery, setRecovery] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!name || !email || !password || !number || !recovery) {
      setError("Veuillez remplir tous les champs !");
      return;
    }

    setLoading(true);
    setError(null);

    console.log("üîπ Donn√©es envoy√©es :", { name, email, password, number, recovery });

    try {
      const response = await axios.post("http://localhost:5000/api/users/register", { 
        name, email, password, number, recovery
      });
      
      alert("‚úÖ Inscription r√©ussie !");
      navigate("/login");
    } catch (error) {
      setError(error.response?.data?.message || "Erreur lors de l'inscription.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
      }}
    >
      {/* üîπ Logo Marvel Store */}
      <img 
        src="/assets/mavelstorelogo.jpg" 
        alt="Marvel Store Logo" 
        style={{ width: "120px", marginBottom: "15px" }} 
      />

      {/* üìå Bloc Connexion */}
      <div
        style={{
          backgroundColor: "#D7C4A1", // üé® M√™me couleur que le logo !
          padding: "30px",
          borderRadius: "12px",
          width: "350px",
          boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.2)",
        }}
      >
        <h2 className="text-2xl font-bold mb-4">üìù Inscription</h2>

        {error && <p style={{ color: "red" }}>‚ö†Ô∏è {error}</p>}

        <form onSubmit={handleSubmit} className="flex flex-col">
          {/* üè∑ Champ Nom */}
          <div className="flex items-center bg-white text-black p-3 rounded mb-3">
            <Icon icon="mdi:account" width="24" className="mr-2" />
            <input
              type="text"
              placeholder="Nom"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="w-full bg-transparent focus:outline-none"
              required
            />
          </div>

          {/* üì® Champ Email */}
          <div className="flex items-center bg-white text-black p-3 rounded mb-3">
            <Icon icon="mdi:email" width="24" className="mr-2" />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full bg-transparent focus:outline-none"
              required
            />
          </div>

          {/* üìû Champ Num√©ro de t√©l√©phone */}
          <div className="flex items-center bg-white text-black p-3 rounded mb-3">
            <Icon icon="mdi:phone" width="24" className="mr-2" />
            <input
              type="text"
              placeholder="Num√©ro de t√©l√©phone"
              value={number}
              onChange={(e) => setNumber(e.target.value)}
              className="w-full bg-transparent focus:outline-none"
              required
            />
          </div>

          {/* üîÑ Champ R√©cup√©ration */}
          <div className="flex items-center bg-white text-black p-3 rounded mb-3">
            <Icon icon="mdi:backup-restore" width="24" className="mr-2" />
            <input
              type="text"
              placeholder="Info de r√©cup√©ration"
              value={recovery}
              onChange={(e) => setRecovery(e.target.value)}
              className="w-full bg-transparent focus:outline-none"
              required
            />
          </div>

          {/* üîë Champ Mot de passe */}
          <div className="flex items-center bg-white text-black p-3 rounded mb-3">
            <Icon icon="mdi:lock" width="24" className="mr-2" />
            <input
              type="password"
              placeholder="Mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full bg-transparent focus:outline-none"
              required
            />
          </div>

          {/* üöÄ Bouton Inscription */}
          <button
            type="submit"
            disabled={loading}
            className="bg-[#3E2F21] hover:bg-[#2D2116] text-white font-bold py-2 px-4 rounded mt-2 flex items-center justify-center"
          >
            {loading ? (
              <>
                <Icon icon="mdi:loading" width="20" className="mr-2 animate-spin" />
                Inscription...
              </>
            ) : (
              <>
                <Icon icon="mdi:account-plus" width="20" className="mr-2" />
                S'inscrire
              </>
            )}
          </button>
        </form>

        {/* üîπ Lien vers la connexion */}
        <p className="mt-3">
          D√©j√† un compte ?{" "}
          <Link to="/login" className="text-[#3E2F21] hover:underline">
            <Icon icon="mdi:login" width="20" className="mr-1" />
            Se connecter
          </Link>
        </p>
      </div>
    </div>
  );
};

export default RegisterPage;
