import { useNavigate } from 'react-router-dom';

function HomePage() {
  const navigate = useNavigate();

  const handleViewProducts = () => {
    console.log("Redirection vers la page de login");
    navigate('/login'); // Redirige vers la page login
  };

  return (
    <div style={{
      //backgroundColor: "red",
      backgroundImage: `url('/assets/homepage.webp')`, // ✅ Vérifie bien ce chemin
      backgroundSize: "cover",
      backgroundPosition: "center",
      backgroundRepeat: "no-repeat",
      minHeight: "100vh",
      display: "flex",
      flexDirection: "column",
      alignItems: "center",
      justifyContent: "center",
      textAlign: "center",
      padding: "20px",
      color: "#fff"
    }}>
      <h1>Bienvenue sur Marvel Store</h1>
      <p>Ajoutez vos produits et profitez du shopping en ligne !</p>
      <button onclick="enterStore()">Entrer dans notre boutique</button>
    </div>
  );
}

export default HomePage;
