import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const CartPage = () => {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const removeFromCart = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  return (
    <div style={{
        backgroundImage: `url('/assets/panier.jpg')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
      <h2 className="text-3xl font-bold mb-6">ðŸ›’ Mon Panier</h2>

      {cart.length === 0 ? (
        <p className="text-xl">Votre panier est vide.</p>
      ) : (
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-4">
          {cart.map((product, index) => (
            <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#035", color: "#fff" }}>
              {/* âœ… Image plus petite */}
              {product.image && (
                <img src={product.image} alt={product.name} className="w-24 h-24 object-cover rounded" />
              )}
              <h3 className="text-md font-bold mt-2 text-center">{product.name}</h3>
              <p className="text-sm">Prix : {product.price}â‚¬</p>
              <button
                onClick={() => removeFromCart(index)}
                className="bg-red-500 text-white px-3 py-1 rounded mt-2 text-sm"
              >
                Supprimer
              </button>
            </div>
          ))}
        </div>
      )}

      <Link to="/products" className="bg-blue-500 text-white px-6 py-3 rounded mt-6">
        ðŸ”™ Retour aux produits
      </Link>
    </div>
  );
};

export default CartPage;
