import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

const ProductPage = () => {
  const [user, setUser] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();

  // Simuler la récupération des infos utilisateur (depuis localStorage ou backend)
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user"));
    if (storedUser) {
      setUser(storedUser);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login"); // Redirection vers la page de connexion
  };

  return (
    <div className="bg-gray-10">
      {/* 🔹 Barre de navigation avec icône utilisateur */}
      <div className=" bg-white ">
        <h1 className="text-xl font-bold">🛍️ Produits</h1>

        {/* ✅ Profil utilisateur */}
        <div className="relative">
          <button onClick={() => setMenuOpen(!menuOpen)} className="flex items-center">
            <img
              src="https://cdn-icons-png.flaticon.com/512/4305/4305692.png" // Icône utilisateur
              
              
            />
            
          </button>

          {/* 🔻 Menu déroulant */}
          {menuOpen && (
            <div className="absolute right-0 mt-2 w-56 bg-white shadow-lg rounded-lg overflow-hidden z-50">
              <ul className="py-2">
                {!user ? (
                  <li>
                    <Link to="/login" className="block px-4 py-2 hover:bg-gray-100">🔐 Login</Link>
                  </li>
                ) : (
                  <>
                    <li>
                      <Link to="/account" className="block px-4 py-2 hover:bg-gray-100">👤 Compte</Link>
                    </li>
                    <li>
                      <Link to="/orders" className="block px-4 py-2 hover:bg-gray-100">📦 Commandes</Link>
                    </li>
                    <li>
                      <Link to="/support" className="block px-4 py-2 hover:bg-gray-100">📞 Assistance Client</Link>
                    </li>
                    <li>
                      <Link to="/wallet" className="block px-4 py-2 hover:bg-gray-100">💳 Portefeuille</Link>
                    </li>
                    <li>
                      <Link to="/addresses" className="block px-4 py-2 hover:bg-gray-100">🏠 Adresses</Link>
                    </li>
                    <li>
                      <button onClick={handleLogout} className="block px-4 py-2 text-red-500 hover:bg-gray-100 w-full text-left">
                        🚪 Déconnexion
                      </button>
                    </li>
                  </>
                )}
              </ul>
            </div>
          )}
        </div>
      </div>

      {/* 🛍️ Liste des produits */}
      <div className="p-6">
        <p>📢 Ici, la liste des produits...</p>
      </div>
    </div>
  );
};

export default ProductPage;
