import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const CartPage = () => {
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  // ðŸ—‘ Supprimer un produit du panier et mettre Ã  jour localStorage
  const removeFromCart = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  return (
    <div style={{
        backgroundImage: `url('/assets/panier.jpg')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
      <h2 className="text-3xl font-bold mb-6">ðŸ›’ Mon Panier</h2>

      {cart.length === 0 ? (
        <p className="text-xl">Votre panier est vide.</p>
      ) : (
        <div className="w-full max-w-lg">
          {cart.map((product, index) => (
            <div key={index} className="border p-4 flex justify-between items-center bg-white mb-4 shadow-lg rounded">
              <div>
                <h3 className="text-lg font-bold">{product.name}</h3>
                <p>Prix : {product.price}â‚¬</p>
              </div>
              <button
                onClick={() => removeFromCart(index)}
                className="bg-red-500 text-white px-3 py-1 rounded"
              >
                Supprimer
              </button>
            </div>
          ))}
        </div>
      )}

      {/* ðŸ”™ Retour Ã  la liste des produits */}
      <Link to="/products" className="bg-blue-500 text-white px-4 py-2 rounded mt-4">ðŸ”™ Retour aux produits</Link>
    </div>
  );
};

export default CartPage;
