import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const response = await axios.post("http://localhost:5000/api/users/login", { email, password });

      if (response.data.success) {
        alert("Connexion r√©ussie !");
        navigate("/dashboard"); // üîÑ Redirection apr√®s connexion
      }
    } catch (error) {
      setError("‚ùå Email ou mot de passe incorrect.");
    }
  };

  return (
    <div
      
      style={{ backgroundImage: `url('/assets/marvelstore.webp')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
       }}
    >
      {/* üîπ Bouton Accueil en haut √† droite */}
      <div className="absolute top-6 left-8">
        <Link to="/" className="text-white text-xl font-bold hover:underline">
          ‚¨Ö Accueil
        </Link>
      </div>

      {/* üîπ Conteneur du formulaire */}
      <div className="bg-black bg-opacity-75 p-10 rounded-lg shadow-lg text-white w-full max-w-lg">
        <h2 className="text-4xl font-bold text-center mb-8">Bienvenue !</h2>

        {/* üîπ Affichage de l'erreur si connexion √©choue */}
        {error && <p className="text-red-500 text-xl text-center mb-6">{error}</p>}

        <form onSubmit={handleSubmit} className="flex flex-col">
          {/* üîπ Champ Email */}
          <div className="mb-6">
            <label className="block text-xl font-semibold mb-2">Email</label>
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full p-4 rounded-lg bg-white text-black text-lg"
              required
            />
          </div>

          {/* üîπ Champ Mot de Passe (bien en dessous du champ Email) */}
          <div className="mb-6">
            <label className="block text-xl font-semibold mb-2">Mot de passe</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full p-4 rounded-lg bg-white text-black text-lg"
              required
            />
          </div>

          {/* üîπ Checkbox "Se souvenir de moi" */}
          <div className="flex items-center mb-6">
            <input
              type="checkbox"
              checked={rememberMe}
              onChange={() => setRememberMe(!rememberMe)}
              className="mr-2"
            />
            <label className="text-lg">Se souvenir de moi</label>
          </div>

          {/* üîπ Bouton Connexion (plus grand et espac√©) */}
          <button type="submit" className="bg-green-700 hover:bg-green-800 p-4 text-lg rounded-lg text-white font-bold mb-6">
            üîì ENTRER
          </button>
        </form>

        {/* üîπ Lien "Mot de passe oubli√©" */}
        <div className="text-center mb-4">
          <Link to="/forgot-password" className="text-white text-lg hover:underline">
            üîë Mot de passe oubli√© ?
          </Link>
        </div>

        {/* üîπ Lien "S'inscrire" */}
        <div className="text-center">
          <span className="text-gray-300 text-lg">Pas encore inscrit ? </span>
          <Link to="/register" className="text-blue-400 text-lg font-bold hover:underline">
            ‚úçÔ∏è S'inscrire
          </Link>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
