import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { Icon } from "@iconify/react";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")));
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedColor, setSelectedColor] = useState("");
  const [size, setSize] = useState("");
  const [quantity, setQuantity] = useState(1);

  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const handleProductClick = (product) => {
    setSelectedProduct(product);
    setSelectedColor("");
    setSize("");
    setQuantity(1);
  };

  const closeProductModal = () => {
    setSelectedProduct(null);
  };

  const addToCart = async () => {
    if (!selectedColor || !size || quantity < 1) {
      alert("Veuillez remplir tous les champs avant d'ajouter au panier.");
      return;
    }
  
    if (quantity > selectedProduct.stock) {
      alert("Stock insuffisant !");
      return;
    }
  
    const cartMap = new Map(cart.map((item) => [`${item._id}-${item.color}-${item.size}`, item]));
    const key = `${selectedProduct._id}-${selectedColor}-${size}`;
  
    if (cartMap.has(key)) {
      cartMap.get(key).quantity += quantity;
    } else {
      cartMap.set(key, { ...selectedProduct, color: selectedColor, size, quantity });
    }
  
    const newCart = Array.from(cartMap.values());
  
    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
    alert("Produit ajout√© au panier !");
    closeProductModal();
  
    // üîπ Et maintenant : aussi ajouter c√¥t√© serveur si l'utilisateur est connect√©
    if (user) {
      try {
        await axios.post("http://localhost:5000/api/cart/add", {
          userId: user._id,
          productId: selectedProduct._id,
          color: selectedColor,
          size,
          quantity,
        });
      } catch (error) {
        console.error("Erreur lors de l'ajout au panier serveur :", error);
      }
    }
  };

  const getDeliveryDate = () => {
    const today = new Date();
    today.setDate(today.getDate() + 2);
    return today.toLocaleDateString("fr-FR", { weekday: "long", day: "numeric", month: "long" });
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", backgroundImage: "url('/assets/marvelstoreproduit.jpeg')", backgroundSize: "cover", backgroundPosition: "center", minHeight: "100vh" }}>
      
      //Barre sup√©rieure Navigation 
      <header style={{ display: "flex", flexDirection: "column", backgroundColor: "#222", color: "white", padding: "10px 1px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        
        {/* üîπ Barre de navigation */}
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          {[
                      { path: "/", icon: "mdi:home" },
                      { path: "/products", icon: "mdi:shopping" },
                      { path: "/cart", icon: "mdi:cart" },
                      { path: "/orders", icon: "mdi:package-variant-closed" },
                      { path: "/account", icon: "mdi:account" },
                      { path: "/wallet", icon: "mdi:credit-card" },
                      { path: "/addresses", icon: "mdi:map-marker" },
                    ].map((item) => (
                      <Link key={item.path} to={item.path} className="nav-link">
                        <Icon icon={item.icon} width="32" color={location.pathname === item.path ? "red" : "white"} />
                      </Link>
                    ))}
        </nav>
        
        {/* üîπ Barre utilisateur */}
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "0 20px" }}>
          <h1>üõçÔ∏è Faites-vous plaisir</h1>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Icon icon="mdi:account-circle" width="40" style={{ cursor: "pointer" }} />
            {user ? (
              <>
                <span style={{ marginLeft: "10px", fontSize: "16px" }}>{user.name}</span>
                <button onClick={handleLogout} style={{ marginLeft: "10px", backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px" }}>D√©connexion</button>
              </>
            ) : (
              <button onClick={() => navigate("/login")} style={{ marginLeft: "10px", backgroundColor: "blue", color: "white", padding: "5px", borderRadius: "3px" }}>Login</button>
            )}
          </div>
        </div>
      </header>
      
      {/* üîπ Contenu principal */}
      <div style={{ 
        backgroundImage: `url('/assets/marvelstoreproduits.jpg')`,
        backgroundSize: "cover",
        marginTop: "120px", 
        flex: 1, 
        padding: "20px" }}>
        <h2>üõçÔ∏è Nos Produits</h2>
        
      {/* üîπ Barre de recherche */}
      <input
        type="text"
        placeholder="üîé Rechercher un produit..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ 
          padding: "10px", 
          marginBottom: "20px", 
          width: "300px",
          borderRadius: "5px",
          border: "1px solid #ccc",
          outline: "none",
        }}
      />

      <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: "20px" }}>
        {products
          .filter((product) =>
            product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            product.description?.toLowerCase().includes(searchTerm.toLowerCase()) // ‚úÖ Recherche aussi sur la description
        )
          .map((product) => (
            <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#000", color: "#fff", cursor: "pointer" }} onClick={() => handleProductClick(product)}>
            {product.image && <img src={`http://localhost:5000${product.image}`} alt={product.name} style={{ width: "150px" }} />}
            <h3>{product.name}</h3>
            <p>Prix : {product.price}‚Ç¨</p>
            <p>Livraison gratuite : <strong>{getDeliveryDate()}</strong></p>
            <p>Description : {product.description}</p>
            </div>
          ))}
      </div>

      </div>

      {/* üîπ Modale produit */}
      {selectedProduct && (
        <div style={{ position: "fixed", top: 0, left: 0, width: "100vw", height: "100vh", backgroundColor: "rgba(0,0,0,0.5)", display: "flex", justifyContent: "center", alignItems: "center" }}>
          <div style={{ backgroundColor: "white", padding: "60px", borderRadius: "50px", width: "200px", height: "450px" }}>
          <img src={`http://localhost:5000${selectedProduct.image}`} alt={selectedProduct.name} style={{ width: "100%"}} />
            <h2>{selectedProduct.name}</h2>
             {/* üîπ Champs en colonne */}
            <div style={{ display: "flex", flexDirection: "column", width: "100%", gap: "0px", alignItems: "center" }}>
                {/* üîπ S√©lection de la couleur */}
                <label>Couleur :</label>
                  <select value={selectedColor} onChange={(e) => setSelectedColor(e.target.value)}>
                    <option value="">S√©lectionnez une couleur</option>
                    {selectedProduct.colors.map((color) => (
                      <option key={color} value={color}>{color}</option>
                    ))}
                  </select>

              {/* üîπ S√©lection de la taille */}
              <label>Taille :</label>
                <select value={size} onChange={(e) => setSize(e.target.value)}>
                  <option value="">S√©lectionnez une taille</option>
                  {selectedProduct.sizes.map((s) => (
                    <option key={s} value={s}>{s}</option>
                  ))}
                </select>
              {/* üîπ Quantit√© */}
              <label>Quantit√© :</label>
              <input 
                type="number" 
                value={quantity} 
                min="1" 
                onChange={(e) => setQuantity(Number(e.target.value))} // ‚úÖ Convertir en nombre
                style={{ width: "80%" }} 
              />
              <p>Stock disponible : <strong>{selectedProduct.stock}</strong></p>

                {/* üîπ Boutons */}
                <button onClick={addToCart} style={{ marginTop: "15px", backgroundColor: "green", color: "white", padding: "10px", borderRadius: "5px", width: "80%" }}>Ajouter au panier</button>
                <button onClick={closeProductModal} style={{ marginTop: "10px", backgroundColor: "red", color: "white", padding: "10px", borderRadius: "5px", width: "80%" }}>Fermer</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductsPage;
