import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import { Icon } from "@iconify/react";

const AccountPage = () => {
  const [user, setUser] = useState({ name: "", email: "", password: "", number: "" });
  const [editMode, setEditMode] = useState({ name: false, email: false, password: false, number: false });
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      try {
        const storedUser = JSON.parse(localStorage.getItem("user"));
        if (!storedUser || !storedUser.email) {
          console.error("‚ùå Aucun utilisateur trouv√© !");
          return;
        }

        const response = await axios.get(`http://localhost:5000/api/users/user/${storedUser.email}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
        });
        setUser(response.data);
      } catch (error) {
        console.error("‚ùå Erreur lors du chargement du compte :", error);
      }
    };
    fetchUser();
  }, []);

  const handleEditToggle = (field) => {
    setEditMode((prev) => ({ ...prev, [field]: !prev[field] }));
  };

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    try {
      await axios.put(`http://localhost:5000/api/users/user/${user.email}`, user, {
        headers: { Authorization: `Bearer ${localStorage.getItem("token")}` },
      });
      alert("‚úÖ Modifications enregistr√©es !");
    } catch (error) {
      console.error("‚ùå Erreur lors de la sauvegarde :", error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div style={{ display: "flex", flexDirection: "column", backgroundImage: "url('/assets/marvelstoreaccount.jpg')", backgroundSize: "cover", minHeight: "100vh", color: "#fff" }}>
      
      {/* üîπ Barre sup√©rieure */}
      <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center", backgroundColor: "#222", padding: "10px 20px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        <Link to="/" style={{ textDecoration: "none", color: "white", fontSize: "24px", display: "flex", alignItems: "center" }}>
          <Icon icon="mdi:storefront" width="40" color="red" />
          <span style={{ marginLeft: "10px", fontWeight: "bold" }}>Marvel Store</span>
        </Link>
        
        {/* üîπ Barre utilisateur */}
        <div style={{ display: "flex", alignItems: "center", gap: "20px" }}>
          <Link to="/cart">
            <Icon icon="mdi:cart" width="32" color="white" />
          </Link>
          <Link to="/account">
            <Icon icon="mdi:account" width="32" color="white" />
          </Link>
          <button onClick={handleLogout} style={{ background: "none", border: "none", cursor: "pointer" }}>
            <Icon icon="mdi:logout" width="32" color="white" />
          </button>
        </div>
      </header>

      {/* üîπ Contenu principal */}
      <div style={{ marginTop: "100px", padding: "20px", display: "flex", flexDirection: "column", alignItems: "center" }}>
        <h2>üë§ Mon Compte</h2>
        {message && <p style={{ color: message.includes("‚ùå") ? "red" : "green" }}>{message}</p>}

        {/* üîπ Informations utilisateur */}
        <div style={{ backgroundColor: "rgba(0,0,0,0.7)", padding: "20px", borderRadius: "10px", width: "80%", maxWidth: "400px" }}>
          
          {/* üîπ Nom */}
          <label>Nom :</label>
          {editMode.name ? (
            <input type="text" name="name" value={user.name} onChange={handleChange} />
          ) : (
            <p>{user.name} <button onClick={() => handleEditToggle("name")}>‚úè Modifier</button></p>
          )}

          {/* üîπ Email */}
          <label>Email :</label>
          {editMode.email ? (
            <input type="text" name="email" value={user.email} onChange={handleChange} />
          ) : (
            <p>{user.email} <button onClick={() => handleEditToggle("email")}>‚úè Modifier</button></p>
          )}

          {/* üîπ Mot de passe */}
          <label>Mot de passe :</label>
          {editMode.password ? (
            <input type="password" name="password" value={user.password} onChange={handleChange} />
          ) : (
            <p>******** <button onClick={() => handleEditToggle("password")}>‚úè Modifier</button></p>
          )}

          {/* üîπ Num√©ro de t√©l√©phone */}
          <label>Num√©ro de t√©l√©phone :</label>
          {editMode.number ? (
            <input type="text" name="number" value={user.number} onChange={handleChange} />
          ) : (
            <p>{user.number || "Non renseign√©"} <button onClick={() => handleEditToggle("number")}>‚úè Modifier</button></p>
          )}

          <button onClick={handleSave} style={{ marginTop: "15px", backgroundColor: "green", color: "white", padding: "10px", borderRadius: "5px", width: "100%" }}>
            üíæ Enregistrer
          </button>
        </div>

        {/* üîπ Liens du compte */}
        <div style={{ marginTop: "20px", backgroundColor: "rgba(0,0,0,0.7)", padding: "20px", borderRadius: "10px", width: "80%", maxWidth: "400px" }}>
          <h3>üìå Options du compte</h3>
          <p><Link to="/orders" style={{ color: "white", textDecoration: "none" }}><Icon icon="mdi:package-variant-closed" width="24" /> Mes Commandes</Link></p>
          <p><Link to="/support" style={{ color: "white", textDecoration: "none" }}><Icon icon="mdi:help-circle" width="24" /> Assistance Client</Link></p>
          <p><Link to="/wallet" style={{ color: "white", textDecoration: "none" }}><Icon icon="mdi:credit-card" width="24" /> Mon Portefeuille</Link></p>
          <p><Link to="/addresses" style={{ color: "white", textDecoration: "none" }}><Icon icon="mdi:home-map-marker" width="24" /> Mes Adresses</Link></p>
        </div>

        <button onClick={() => navigate("/products")} style={{ marginTop: "20px", backgroundColor: "gray", color: "white", padding: "10px", borderRadius: "5px", width: "80%", maxWidth: "400px" }}>
          ‚¨Ö Retour aux Produits
        </button>
      </div>
    </div>
  );
};

export default AccountPage;
