import React, { useState } from "react";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log("Tentative de connexion avec :", { email, password }); // ✅ Vérifier si la fonction est appelée

    try {
      const response = await axios.post("http://localhost:5000/api/users/login", { email, password });
      console.log("Réponse du serveur :", response.data); // ✅ Voir la réponse du backend
      alert("Connexion réussie !");
    } catch (error) {
      console.error("Erreur :", error.response?.data?.message || "Problème serveur");
      alert("Erreur : " + error.response?.data?.message || "Problème serveur");
    }
  };

  return (
    <div>
      <h2>Se connecter</h2>
      <form onSubmit={handleSubmit}>
        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} required />
        <input type="password" placeholder="Mot de passe" value={password} onChange={(e) => setPassword(e.target.value)} required />
        <button type="submit">Se connecter</button>
      </form>
    </div>
  );
};

export default LoginPage;
