import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { Icon } from "@iconify/react";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")));
  const [userMenuOpen, setUserMenuOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [selectedColor, setSelectedColor] = useState("");
  const [selectedSize, setSelectedSize] = useState("");
  const [quantity, setQuantity] = useState(1);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const handleProductClick = (product) => {
    setSelectedProduct(product);
    setSelectedColor("");
    setSelectedSize("");
    setQuantity(1);
  };

  const closeProductModal = () => {
    setSelectedProduct(null);
  };

  const handleAddToCart = () => {
    if (!selectedColor || (!selectedSize && selectedProduct.category !== "informatique") || quantity < 1) return;

    const newCart = [...cart, {
      _id: selectedProduct._id,
      name: selectedProduct.name,
      price: selectedProduct.price,
      image: selectedProduct.image,
      color: selectedColor,
      size: selectedSize,
      quantity: quantity
    }];

    setCart(newCart);
    localStorage.setItem("cart", JSON.stringify(newCart));
    closeProductModal();
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div style={{
        display: "flex",
        flexDirection: "column",
        backgroundImage: "url('/mnt/data/marvelstoreproduits.jpeg')",
        backgroundSize: "cover",
        backgroundPosition: "center",
        minHeight: "100vh"
      }}>
      
      {/* Barre sup√©rieure */}
      <header style={{
        display: "flex",
        flexDirection: "column",
        backgroundColor: "#222",
        color: "white",
        padding: "10px 0",
        position: "fixed",
        top: 0,
        left: 0,
        width: "100%",
        zIndex: 10
      }}>
        {/* Navigation */}
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          <Link to="/"><Icon icon="mdi:home" width="32" /></Link>
          <Link to="/products"><Icon icon="mdi:shopping" width="32" /></Link>
          <Link to="/cart"><Icon icon="mdi:cart" width="32" /></Link>
          <Link to="/account"><Icon icon="mdi:account" width="32" /></Link>
          <Link to="/wallet"><Icon icon="mdi:credit-card" width="32" /></Link>
          <Link to="/addresses"><Icon icon="mdi:map-marker" width="32" /></Link>
        </nav>

        {/* Ic√¥ne utilisateur */}
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "0 20px" }}>
          <h1>üõçÔ∏è Faites-vous plaisir</h1>

          <div style={{ display: "flex", alignItems: "center", position: "relative", cursor: "pointer" }} onClick={() => setUserMenuOpen(!userMenuOpen)}>
            <Icon icon="mdi:account-circle" width="40" color="red" />
            {user ? (
              <>
                <span style={{ marginLeft: "10px" }}>{user.name}</span>
                {userMenuOpen && (
                  <div style={{
                    position: "absolute",
                    top: "50px",
                    right: "0",
                    backgroundColor: "white",
                    color: "black",
                    padding: "10px",
                    borderRadius: "5px",
                    boxShadow: "0px 0px 10px rgba(0,0,0,0.1)"
                  }}>
                    <p><strong>Nom :</strong> {user.name}</p>
                    <button onClick={handleLogout} style={{
                      backgroundColor: "red",
                      color: "white",
                      padding: "5px",
                      borderRadius: "3px",
                      width: "100%"
                    }}>D√©connexion</button>
                  </div>
                )}
              </>
            ) : (
              <button onClick={() => navigate("/login")} style={{ marginLeft: "10px", backgroundColor: "blue", color: "white", padding: "5px", borderRadius: "3px" }}>Login</button>
            )}
          </div>
        </div>
      </header>

      <div style={{ marginTop: "120px", flex: 1, padding: "20px" }}>
        <input
          type="text"
          placeholder="Rechercher un produit..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ padding: "10px", marginBottom: "20px", width: "300px" }}
        />

        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: "20px" }}>
          {filteredProducts.map((product) => (
            <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#000", color: "#fff", cursor: "pointer" }}
              onClick={() => handleProductClick(product)}>
              <h3>{product.name}</h3>
              <p>Prix : {product.price}‚Ç¨</p>
              {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
            </div>
          ))}
        </div>
      </div>

      {/* Modale produit */}
      {selectedProduct && (
        <div style={{
          position: "fixed", top: 0, left: 0, width: "100%", height: "100%",
          backgroundColor: "rgba(0,0,0,0.5)", display: "flex", justifyContent: "center", alignItems: "center"
        }}>
          <div style={{ backgroundColor: "white", padding: "20px", borderRadius: "10px", width: "400px" }}>
            <h2>{selectedProduct.name}</h2>
            <img src={selectedProduct.image} alt={selectedProduct.name} style={{ width: "100%" }} />
            <p>{selectedProduct.description}</p>

            <label>Couleur :</label>
            <select value={selectedColor} onChange={(e) => setSelectedColor(e.target.value)}>
              <option value="">S√©lectionnez une couleur</option>
              <option value="Rouge">Rouge</option>
              <option value="Bleu">Bleu</option>
              <option value="Noir">Noir</option>
              <option value="Blanc">Blanc</option>
            </select>

            {selectedProduct.category !== "informatique" && (
              <>
                <label>Taille :</label>
                <select value={selectedSize} onChange={(e) => setSelectedSize(e.target.value)}>
                  {Array.from({ length: 10 }, (_, i) => 36 + i).map(size => <option key={size}>{size}</option>)}
                </select>
              </>
            )}

            <button onClick={handleAddToCart} disabled={!selectedColor || (!selectedSize && selectedProduct.category !== "informatique")}>
              Ajouter au panier
            </button>
            <button onClick={closeProductModal}>Fermer</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductsPage;
