import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Icon } from "@iconify/react"; // Assurez-vous d'avoir installé @iconify/react

const CartPage = () => {
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState(null);
  const [address, setAddress] = useState("");
  const [paymentMethod, setPaymentMethod] = useState("card");
  const [cardDetails, setCardDetails] = useState("");
  const [paypalEmail, setPaypalEmail] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  useEffect(() => {
    const storedCart = JSON.parse(localStorage.getItem("cart")) || [];
    setCart(storedCart);
  }, []);

  const removeFromCart = (index) => {
    const updatedCart = [...cart];
    updatedCart.splice(index, 1);
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const getDeliveryDate = () => {
    const today = new Date();
    today.setDate(today.getDate() + 2);
    return today.toLocaleDateString("fr-FR", { weekday: "long", day: "numeric", month: "long" });
  };

  const handlePayment = () => {
    alert("Paiement effectué avec succès ! Votre commande arrivera le " + getDeliveryDate());
  };

  return (
    <div style={{
        backgroundImage: `url('/assets/marvelstoreaccount.jpg')`,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        textAlign: "center",
        padding: "20px",
        color: "#fff"
      }}>
      {/* Barre supérieure + Navigation */}
      <header style={{ display: "flex", flexDirection: "column", backgroundColor: "#222", color: "white", padding: "10px 1px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          <Link to="/" className="nav-link">
            <Icon icon="mdi:home" width="32" color={location.pathname === "/" ? "red" : "white"} />
          </Link>
          <Link to="/products" className="nav-link">
            <Icon icon="mdi:shopping" width="32" color={location.pathname === "/products" ? "red" : "white"} />
          </Link>
          <Link to="/cart" className="nav-link">
            <Icon icon="mdi:cart" width="32" color={location.pathname === "/cart" ? "red" : "white"} />
          </Link>
          <Link to="/account" className="nav-link">
            <Icon icon="mdi:account" width="32" color={location.pathname === "/account" ? "red" : "white"} />
          </Link>
          <Link to="/wallet" className="nav-link">
            <Icon icon="mdi:credit-card" width="32" color={location.pathname === "/wallet" ? "red" : "white"} />
          </Link>
          <Link to="/addresses" className="nav-link">
            <Icon icon="mdi:map-marker" width="32" color={location.pathname === "/addresses" ? "red" : "white"} />
          </Link>
        </nav>
        
        {/* Barre utilisateur */}
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "0 20px" }}>
          <h1>🛍️ La meilleure Boutique</h1>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Icon icon="mdi:account-circle" width="40" style={{ cursor: "pointer" }} />
            {user ? (
              <>
                <span style={{ marginLeft: "10px", fontSize: "16px" }}>{user.name}</span>
                <button onClick={handleLogout} style={{ marginLeft: "10px", backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px" }}>Déconnexion</button>
              </>
            ) : (
              <button onClick={() => navigate("/login")} style={{ marginLeft: "10px", backgroundColor: "blue", color: "white", padding: "5px", borderRadius: "3px" }}>Login</button>
            )}
          </div>
        </div>
      </header>

      <main className="mt-20 px-4 flex flex-col items-center">
        <h2 className="text-3xl font-bold mb-6">🛒 Mon Panier</h2>

        {cart.length === 0 ? (
          <p className="text-xl">Votre panier est vide.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 w-full max-w-4xl">
            {cart.map((product, index) => (
              <div key={index} className="border p-4 flex flex-col items-center bg-white shadow-lg rounded">
                <img src={product.image} alt={product.name} className="w-40 h-40 object-cover mb-2" />
                <h3 className="text-lg font-bold">{product.name}</h3>
                <p>Prix : {product.price}€</p>
                <button
                  onClick={() => removeFromCart(index)}
                  className="bg-red-500 text-white px-3 py-1 rounded mt-2"
                >
                  Supprimer
                </button>
              </div>
            ))}
          </div>
        )}

        {cart.length > 0 && (
          <div className="mt-6 p-4 bg-white shadow-lg rounded w-full max-w-md">
            <h3 className="text-xl font-bold mb-4">💳 Paiement</h3>
            <label className="block mb-2">Adresse de livraison:</label>
            <input
              type="text"
              className="w-full p-2 border rounded mb-4"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
              placeholder="Votre adresse complète"
            />

            <label className="block mb-2">Mode de paiement:</label>
            <select
              className="w-full p-2 border rounded mb-4"
              value={paymentMethod}
              onChange={(e) => setPaymentMethod(e.target.value)}
            >
              <option value="card">Carte bancaire</option>
              <option value="paypal">PayPal</option>
            </select>

            {paymentMethod === "card" && (
              <input
                type="text"
                className="w-full p-2 border rounded mb-4"
                value={cardDetails}
                onChange={(e) => setCardDetails(e.target.value)}
                placeholder="Numéro de carte"
              />
            )}

            {paymentMethod === "paypal" && (
              <input
                type="email"
                className="w-full p-2 border rounded mb-4"
                value={paypalEmail}
                onChange={(e) => setPaypalEmail(e.target.value)}
                placeholder="Email PayPal"
              />
            )}

            <p className="mb-4">📅 Date de livraison prévue: <strong>{getDeliveryDate()}</strong></p>

            <button
              onClick={handlePayment}
              className="w-full bg-green-500 text-white p-3 rounded"
            >
              Effectuer le paiement
            </button>
          </div>
        )}
      </main>
    </div>
  );
};

export default CartPage;
