import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";
import { Icon } from "@iconify/react";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")) || []);
  const [user, setUser] = useState(JSON.parse(localStorage.getItem("user")));
  const [menuOpen, setMenuOpen] = useState(false);
  const [userMenuOpen, setUserMenuOpen] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [size, setSize] = useState("");
  const [color, setColor] = useState("");
  const [quantity, setQuantity] = useState(1);
  const navigate = useNavigate();

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const openModal = (product) => {
    setSelectedProduct(product);
    setSize("");
    setColor("");
    setQuantity(1);
  };

  const closeModal = () => {
    setSelectedProduct(null);
  };

  const addToCart = () => {
    if (!selectedProduct) return;
    
    const newItem = {
      _id: selectedProduct._id,
      name: selectedProduct.name,
      price: selectedProduct.price,
      image: selectedProduct.image,
      size: size || null,
      color: color || null,
      quantity: quantity,
    };

    const updatedCart = [...cart, newItem];
    setCart(updatedCart);
    localStorage.setItem("cart", JSON.stringify(updatedCart));
    alert(`${selectedProduct.name} a été ajouté au panier !`);
    closeModal();
  };

  return (
    <div style={{ display: "flex", flexDirection: "column" }}>
      {/* 🔹 Barre supérieure */}
      <header style={{ display: "flex", justifyContent: "space-between", alignItems: "center", backgroundColor: "#222", color: "white", padding: "10px 20px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        <h1>🛍️ Produits</h1>
        
        {/* 🔹 Icône utilisateur */}
        {user && (
          <div style={{ display: "flex", alignItems: "center", cursor: "pointer", position: "relative" }} onClick={() => setUserMenuOpen(!userMenuOpen)}>
            <div style={{ position: "relative" }}>
              <Icon icon="mdi:account-circle" width="40" />
              <span style={{ position: "absolute", top: "2px", right: "2px", backgroundColor: "green", width: "10px", height: "10px", borderRadius: "50%" }}></span>
            </div>
            <span style={{ marginLeft: "10px" }}>{user.name}</span>

            {userMenuOpen && (
              <div style={{ position: "absolute", top: "50px", right: "0", backgroundColor: "white", color: "black", padding: "10px", borderRadius: "5px", boxShadow: "0px 0px 10px rgba(0,0,0,0.1)" }}>
                <p><strong>Nom :</strong> {user.name}</p>
                <p><strong>Téléphone :</strong> {user.phone}</p>
                <button onClick={handleLogout} style={{ backgroundColor: "red", color: "white", padding: "5px", borderRadius: "3px", width: "100%" }}>Déconnexion</button>
              </div>
            )}
          </div>
        )}
      </header>
      
      <div style={{ display: "flex", marginTop: "60px" }}>
        <div style={{ marginLeft: "100px", flex: 1, padding: "20px" }}>
          <h2>🛍️ Nos Produits</h2>
          
          <input
            type="text"
            placeholder="Rechercher un produit..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            style={{ padding: "10px", marginBottom: "20px", width: "300px" }}
          />

          <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", marginTop: "20px" }}>
            {filteredProducts.map((product) => (
              <div key={product._id} style={{ border: "1px solid #ccc", padding: "10px", margin: "10px", backgroundColor: "#000", color: "#fff" }}>
                <h3>{product.name}</h3>
                <p>Prix : {product.price}€</p>
                {product.image && <img src={product.image} alt={product.name} style={{ width: "150px" }} />}
                <button onClick={() => openModal(product)} className="bg-green-500 text-white px-4 py-2 rounded mt-2">
                  Ajouter au panier
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>

      {selectedProduct && (
        <div style={{ position: "fixed", top: 0, left: 0, width: "100%", height: "100%", backgroundColor: "rgba(0, 0, 0, 0.5)", display: "flex", justifyContent: "center", alignItems: "center" }}>
          <div style={{ backgroundColor: "white", padding: "20px", borderRadius: "10px" }}>
            <h2>{selectedProduct.name}</h2>
            <p>{selectedProduct.description}</p>
            {selectedProduct.category === "chaussure" || selectedProduct.category === "t-shirt" ? (
              <>
                <label>Taille:</label>
                <input type="text" value={size} onChange={(e) => setSize(e.target.value)} />
                <label>Couleur:</label>
                <input type="text" value={color} onChange={(e) => setColor(e.target.value)} />
              </>
            ) : null}
            <label>Quantité:</label>
            <input type="number" value={quantity} onChange={(e) => setQuantity(e.target.value)} />
            <button onClick={addToCart}>Confirmer</button>
            <button onClick={closeModal}>Annuler</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductsPage;
