import React, { useEffect, useState } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { Icon } from "@iconify/react";
import axios from "axios";

const CartPage = () => {
  const [cart, setCart] = useState(() => {
    const local = JSON.parse(localStorage.getItem("cart"));
    return local ? { products: local } : { products: [] };
  });  
  const [user, setUser] = useState(() => JSON.parse(localStorage.getItem("user")) || null);
  const [address, setAddress] = useState("");
  const [savedAddresses, setSavedAddresses] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState("card");
  const [selectedCardId, setSelectedCardId] = useState("");
  const [savedCards, setSavedCards] = useState([]);
  const [paypalEmail, setPaypalEmail] = useState("");

  const navigate = useNavigate();
  const location = useLocation();

  const loadPayPalScript = () => {
    return new Promise((resolve, reject) => {
      if (document.getElementById("paypal-sdk")) return resolve();
  
      const script = document.createElement("script");
      script.src = "https://www.paypal.com/sdk/js?client-id=Aa1Ocb6Gn5RgWkO5LbrJ-H&currency=EUR"; // ✅ Ton Client ID ici
      script.id = "paypal-sdk";
      script.onload = () => resolve();
      script.onerror = (err) => reject(err);
      document.body.appendChild(script);
    });
  };  

  useEffect(() => {
    if (!user) return;
  
    // Charger le panier
    axios.get(`http://localhost:5000/api/cart/${user._id}`)
      .then(res => {
        const products = res.data?.products || [];
        setCart({ products });
        localStorage.setItem("cart", JSON.stringify(products));
      })
      .catch(err => console.error("Erreur panier :", err));
  
    // Charger les adresses
    axios.get(`http://localhost:5000/api/addresses`, { params: { userId: user._id } })
      .then(res => setSavedAddresses(res.data))
      .catch(err => console.error("Erreur adresses :", err));
  
    // Charger les cartes
    axios.get(`http://localhost:5000/api/cards/${user._id}`)
      .then(res => setSavedCards(res.data))
      .catch(err => console.error("Erreur cartes :", err));
  }, [user]);
  
  useEffect(() => {
    const loadPayPalScript = () => {
      return new Promise((resolve, reject) => {
        if (document.getElementById("paypal-sdk")) return resolve();
  
        const script = document.createElement("script");
        script.src = "https://www.paypal.com/sdk/js?client-id=Aa1Ocb6Gn5RgWkO5LbrJ-H&currency=EUR";
        script.id = "paypal-sdk";
        script.onload = () => resolve();
        script.onerror = (err) => reject(err);
        document.body.appendChild(script);
      });
    };
  
    if (paymentMethod === "paypal") {
      loadPayPalScript()
        .then(() => {
          if (window.paypal) {
            window.paypal.Buttons({
              createOrder: (data, actions) => {
                return actions.order.create({
                  purchase_units: [{
                    amount: {
                      value: getTotalPrice()
                    }
                  }]
                });
              },
              onApprove: (data, actions) => {
                return actions.order.capture().then((details) => {
                  alert("Paiement PayPal confirmé par " + details.payer.name.given_name);
                  handlePaypalSuccess(); // 👈 ici on appelle la fonction après succès
                });
              }              
            }).render("#paypal-button-container");
          }
        })
        .catch(err => console.error("Erreur de chargement PayPal :", err));
    }
  }, [paymentMethod, cart]);
  
  const handlePaypalSuccess = async () => {
    try {
      // 1. Mettre à jour le stock
      await axios.post("http://localhost:5000/api/cart/updateStock", { cart });
  
      // 2. Créer une commande
      await axios.post("http://localhost:5000/api/orders/create", {
        userId: user._id,
        cart,
        address,
        paymentMethod: "paypal",
        total: getTotalPrice(),
      });
  
      // 3. Vider le panier côté backend
      await axios.post("http://localhost:5000/api/cart/clear", { userId: user._id });
  
      // 4. Vider le panier côté frontend
      setCart({ products: [] });
      localStorage.removeItem("cart");
  
      // 5. Rediriger ou informer
      alert("✅ Paiement PayPal réussi et commande créée !");
      navigate("/orders");
    } catch (err) {
      console.error("Erreur après paiement PayPal :", err);
      alert("❌ Une erreur est survenue après le paiement.");
    }
  };  

  const removeFromCart = async (product) => {
    try {
      await axios.post("http://localhost:5000/api/cart/remove", {
        userId: user._id,
        productId: product.productId._id || product.productId,
        color: product.color,
        size: product.size
      });
  
      const newCart = cart.products.filter(
        (item) =>
          !(
            item.productId._id === product.productId._id &&
            item.color === product.color &&
            item.size === product.size
          )
      );
  
      setCart({ products: newCart });
      localStorage.setItem("cart", JSON.stringify(newCart));
    } catch (error) {
      console.error("Erreur suppression :", error);
      alert("Erreur lors de la suppression du produit.");
    }
  };  

  // 👇 Fonction pour calculer le total
  const getTotalPrice = () => {
    if (!Array.isArray(cart.products)) return 0;
  
    return cart.products.reduce((total, item) => {
      const price = item?.productId?.price || 0;
      const quantity = item?.quantity || 0;
      return total + price * quantity;
    }, 0).toFixed(2);
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  const getDeliveryDate = () => {
    const date = new Date();
    date.setDate(date.getDate() + 2);
    return date.toLocaleDateString("fr-FR", { weekday: "long", day: "numeric", month: "long" });
  };

  const handlePayment = async () => {
    if (!address) return alert("Sélectionnez une adresse.");
  
    if (paymentMethod === "paypal") {
      window.location.href = "https://www.paypal.me/marvelstore1010";
      return;
    }

    console.log("🎯 Données envoyées à Stripe :", {
      cart,
      userEmail: user.email,
    });
  
    try {
      const response = await axios.post("http://localhost:5000/api/stripe/create-checkout-session", {
        cart,
        userEmail: user.email,
      });
  
      window.location.href = response.data.url;
    } catch (err) {
      console.error("Erreur de paiement :", err);
      alert("Erreur lors du paiement.");
    }
  };
  
  return (
    <div>
      {/* Navigation */}
      <header style={{ backgroundColor: "#222", color: "white", padding: "10px 1px", position: "fixed", top: 0, left: 0, width: "100%", zIndex: 10 }}>
        <nav style={{ display: "flex", justifyContent: "center", gap: "20px", padding: "10px 0" }}>
          {["/", "/products", "/cart", "/orders", "/account", "/wallet", "/addresses"].map((path, idx) => (
            <Link key={path} to={path}>
              <Icon icon={`mdi:${["home", "shopping", "cart", "package-variant-closed", "account", "credit-card", "map-marker"][idx]}`} width="32" color={location.pathname === path ? "red" : "white"} />
            </Link>
          ))}
        </nav>
        <div style={{ display: "flex", justifyContent: "space-between", padding: "0 20px", alignItems: "center" }}>
          <h1>🛒 Mon Panier</h1>
          <div style={{ display: "flex", alignItems: "center" }}>
            <Icon icon="mdi:account-circle" width="40" />
            {user ? (
              <>
                <span style={{ marginLeft: "10px" }}>{user.name}</span>
                <button onClick={handleLogout} style={{ marginLeft: "10px", backgroundColor: "red", color: "white" }}>Déconnexion</button>
              </>
            ) : (
              <button onClick={() => navigate("/login")} style={{ marginLeft: "10px", backgroundColor: "blue", color: "white" }}>Se connecter</button>
            )}
          </div>
        </div>
      </header>

      <main style={{ marginTop: "120px", padding: "20px", backgroundColor: "#111", minHeight: "100vh", color: "white" }}>
        {cart.products.length === 0 ? (
          <h2>Votre panier est vide.</h2>
        ) : (
          <>
            <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
              {cart.products.map((product, i) => (
                <div key={i} style={{ backgroundColor: "#222", margin: 10, padding: 10 }}>
                  <img src={`http://localhost:5000${product.productId?.image}`} alt={product.productId?.name} style={{ width: "180px", height: "150px" }} />
                  <h3>{product.productId?.name}</h3>
                  <p>Prix : {product.productId?.price}€</p>
                  <p>Couleur : {product.color}</p>
                  <p>Taille : {product.size}</p>
                  <p>Quantité : {product.quantity}</p>
                  <button onClick={() => removeFromCart(product)} style={{ backgroundColor: "red", color: "white" }}>Supprimer</button>
                </div>
              ))}
            </div>
            
            <div style={{ fontWeight: "bold", fontSize: "18px", marginTop: "20px" }}>
              Total : {getTotalPrice()} €
            </div>


            {/* Paiement */}
            <div style={{ backgroundColor: "#222", padding: 20, marginTop: 30, borderRadius: 10 }}>
              <h3>💳 Paiement</h3>

              {/* Adresse */}
              <label>Adresse enregistrée :</label>
              <select value={address} onChange={(e) => setAddress(e.target.value)} style={{ width: "60%", marginBottom: 20 }}>
                <option value="">-- Sélectionnez une adresse --</option>
                {savedAddresses.map((addr, i) => (
                  <option key={i} value={`${addr.fullName}, ${addr.address}, ${addr.city}`}>
                    {addr.fullName}, {addr.address}, {addr.city}
                  </option>
                ))}
              </select>

              {/* Paiement */}
              <label>Moyen de paiement :</label>
              <select value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)} style={{ width: "30%", marginBottom: 20 }}>
                <option value="card">Carte</option>
                <option value="paypal">PayPal</option>
              </select>

              {paymentMethod === "card" && (
                <select value={selectedCardId} onChange={(e) => setSelectedCardId(e.target.value)} style={{ width: "50%", marginBottom: 20 }}>
                  <option value="">-- Sélectionnez une carte --</option>
                  {savedCards.map((card) => (
                    <option key={card._id} value={card._id}>
                      {card.holder} - **** {card.number.slice(-4)} - {card.expiryMonth}/{card.expiryYear}
                    </option>
                  ))}
                </select>
              )}

              {paymentMethod === "paypal" && (
                <div id="paypal-button-container" style={{ marginTop: 10 }}></div>
              )}

              <p>📦 Livraison prévue : {getDeliveryDate()}</p>
              <button onClick={handlePayment} style={{ backgroundColor: "green", color: "white" }}>Payer</button>
            </div>
          </>
        )}
      </main>
    </div>
  );
};

export default CartPage;
