import { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function LoginPage() {
  const [isRegistering, setIsRegistering] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [surname, setSurname] = useState('');
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isRegistering) {
      console.log('Inscription:', { name, surname, email, password });
    } else {
      console.log('Connexion:', { email, password });
    }
    // Simule une authentification et redirige vers la page des produits
    localStorage.setItem('isAuthenticated', true);
    navigate('/products');
  };

  return (
    <div>
      <h2>{isRegistering ? "S'inscrire" : "Se connecter"}</h2>
      <form onSubmit={handleSubmit}>
        {isRegistering && (
          <>
            <input 
              type="text" 
              placeholder="Nom" 
              value={name} 
              onChange={(e) => setName(e.target.value)} 
              required 
            />
            <input 
              type="text" 
              placeholder="Prénom" 
              value={surname} 
              onChange={(e) => setSurname(e.target.value)} 
              required 
            />
          </>
        )}
        <input 
          type="email" 
          placeholder="Email" 
          value={email} 
          onChange={(e) => setEmail(e.target.value)} 
          required 
        />
        <input 
          type="password" 
          placeholder="Mot de passe" 
          value={password} 
          onChange={(e) => setPassword(e.target.value)} 
          required 
        />
        <button type="submit">{isRegistering ? "S'inscrire" : "Se connecter"}</button>
      </form>
      <p onClick={() => setIsRegistering(!isRegistering)}>
        {isRegistering ? "Déjà un compte ? Connectez-vous" : "Pas encore de compte ? Inscrivez-vous"}
      </p>
    </div>
  );
}

export default LoginPage;
