import React, { useState, useEffect } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import Select from "react-select";
import { Icon } from "@iconify/react";

const italianCities = [
  { value: "Parma", label: "Parma" },
  { value: "Bologna", label: "Bologna" },
  { value: "Modena", label: "Modena" },
  { value: "Reggio Emilia", label: "Reggio Emilia" },
  { value: "Ferrara", label: "Ferrara" },
];

const AddressesPage = () => {
  const [country, setCountry] = useState("");
  const [fullName, setFullName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [address, setAddress] = useState("");
  const [selectedCity, setSelectedCity] = useState(null);
  const [zipCode, setZipCode] = useState("");
  const [province, setProvince] = useState("");
  const [consent, setConsent] = useState(false);
  const [user, setUser] = useState(null);
  const [addresses, setAddresses] = useState([]);
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const storedUser = localStorage.getItem("user");
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);
      fetchAddresses(parsedUser._id);
    } else {
      navigate("/login");
    }
  }, [navigate]);

  const fetchAddresses = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5000/api/addresses?userId=${userId}`);
      const data = await response.json();
      setAddresses(data);
    } catch (error) {
      console.error("Error retrieving addresses", error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!user || !user._id) return alert("Error: User not logged in !");
    if (!country || !fullName || !phoneNumber || !address || !selectedCity || !zipCode || !province) {
      return alert("Please fill in all required fields..");
    }
    try {
      const response = await fetch("http://localhost:5000/api/addresses", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          userId: user._id,
          country,
          fullName,
          phoneNumber,
          address,
          city: selectedCity.value,
          zipCode,
          province,
        }),
      });
      const data = await response.json();
      if (response.ok) alert("Address save !");
      else alert("Error: " + data.message);
    } catch (error) {
      console.error("Error adding address:", error);
      alert("An error occurred..");
    }
  };

  const handleDelete = async (id) => {
    if (!id || !user || !user._id) return alert("Error: User not logged in !");
    try {
      const response = await fetch(`http://localhost:5000/api/addresses/${id}`, {
        method: "DELETE",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${user?.token}`,
        },
        body: JSON.stringify({ userId: user._id }),
      });
      const data = await response.json();
      if (!response.ok) throw new Error(data.message || "Erreur lors de la suppression");
      setAddresses((prev) => prev.filter((addr) => addr._id !== id));
      alert("Adresse supprim√©e !");
    } catch (error) {
      console.error("Erreur suppression :", error.message);
      alert(error.message);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("user");
    setUser(null);
    navigate("/login");
  };

  return (
    <div style={{
      display: "flex",
      minHeight: "100vh",
      backgroundImage: `url('/assets/marvelstoreaddresse.jpeg')`,
      backgroundSize: "cover",
      backgroundPosition: "center",
      color: "#fff"
    }}>
      {/* Left Sidebar */}
      <aside style={{
        width: "250px",
        backgroundColor: "#222",
        color: "white",
        display: "flex",
        flexDirection: "column",
        justifyContent: "space-between",
        position: "fixed",
        left: 0,
        top: 0,
        bottom: 0,
        padding: "20px 10px",
        zIndex: 10
      }}>
        {/* Navigation icons */}
        <nav style={{ display: "flex", flexDirection: "column", gap: "20px" }}>
          {[{ path: "/", icon: "mdi:home" },
          { path: "/products", icon: "mdi:shopping" },
          { path: "/cart", icon: "mdi:cart" },
          { path: "/orders", icon: "mdi:package-variant-closed" },
          { path: "/account", icon: "mdi:account" },
          { path: "/addresses", icon: "mdi:map-marker" }].map((item) => (
            <Link key={item.path} to={item.path}>
              <Icon icon={item.icon} width="32" color={location.pathname === item.path ? "red" : "white"} />
            </Link>
          ))}
        </nav>

        {/* User Info and Logout */}
        <div>
          <div style={{ display: "flex", alignItems: "center", marginBottom: "10px" }}>
            <Icon icon="mdi:account-circle" width="40" />
            <span style={{ marginLeft: "10px" }}>{user?.name || "User"}</span>
          </div>
          <button onClick={handleLogout} style={{
            backgroundColor: "red",
            color: "white",
            padding: "8px 12px",
            border: "none",
            borderRadius: "5px",
            width: "100%"
          }}>Disconnection</button>
        </div>
      </aside>

      {/* Main Content */}
      <main style={{ marginLeft: "270px", width: "100%", padding: "40px 20px" }}>
        <h2>üè† My Address</h2>
        <p>Add or change your shipping addresses</p>
        <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px", maxWidth: "500px", margin: "auto", textAlign: "left" }}>
          <select value={country} onChange={(e) => setCountry(e.target.value)} required>
            <option value="">Select a country</option>
            <option value="Italie">Italy</option>
          </select>
          <input type="text" placeholder="First name and last name" value={fullName} onChange={(e) => setFullName(e.target.value)} required />
          <input type="tel" placeholder="Number" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} required />
          <Select options={italianCities} value={selectedCity} onChange={setSelectedCity} placeholder="Select a city" isSearchable />
          <input type="text" placeholder="Address" value={address} onChange={(e) => setAddress(e.target.value)} required />
          <input type="text" placeholder="Postal code" value={zipCode} onChange={(e) => setZipCode(e.target.value)} required />
          <input type="text" placeholder="Province" value={province} onChange={(e) => setProvince(e.target.value)} required />
          <label>
            <input type="checkbox" checked={consent} onChange={() => setConsent(!consent)} required />
            I agree that this address will be used for delivery.
          </label>
          <button type="submit">Save address</button>
        </form>

        {/* Address List */}
        {addresses.length > 0 ? (
          <ul style={{ marginTop: "20px" }}>
            {addresses.map((addr) => (
              <li key={addr._id}>
                {addr.fullName} - {addr.address}, {addr.city} ({addr.zipCode})
                <button onClick={() => handleDelete(addr._id)} style={{ marginLeft: "10px", backgroundColor: "darkred", color: "white", border: "none", padding: "5px 10px", borderRadius: "3px" }}>Remove</button>
              </li>
            ))}
          </ul>
        ) : (
          <p>No registered address.</p>
        )}
      </main>
    </div>
  );
};

export default AddressesPage;
