import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { Icon } from "@iconify/react";
import axios from "axios";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!email || !password) {
      setError("Veuillez remplir tous les champs !");
      return;
    }

    setLoading(true);
    setError(null);

    try {
      const response = await axios.post("http://localhost:5000/api/users/login", { email, password });

      // ✅ Sauvegarde de l'utilisateur dans localStorage
      localStorage.setItem("user", JSON.stringify(response.data.user));

      alert("✅ Connexion réussie !");
      navigate("/");
    } catch (error) {
      console.error("Erreur complète :", error);
      setError(error.response?.data?.message || "Erreur lors de la connexion.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        justifyContent: "center",
        textAlign: "center",
        padding: "20px",
        color: "#3E2F21", // Même couleur que le logo
      }}
    >
      {/* 🔹 Liens Accueil / Produits */}
      <div className="absolute top-4 right-4 flex gap-4">
        <Link to="/" className="text-white text-lg font-bold hover:underline">
          <Icon icon="mdi:home" width="24" className="mr-1" /> Accueil
        </Link>
        <Link to="/products" className="text-white text-lg font-bold hover:underline">
          <Icon icon="mdi:shopping" width="24" className="mr-1" /> Produits
        </Link>
      </div>

      {/* 📌 Bloc Connexion */}
      <div
        style={{
          backgroundColor: "#D7C4A1", // 🎨 Même couleur que le logo !
          padding: "30px",
          borderRadius: "12px",
          width: "350px",
          boxShadow: "0px 4px 10px rgba(0, 0, 0, 0.2)",
        }}
      >
        {/* 🔹 Ajout du logo Marvel Store */}
        <img 
          src="/assets/mavelstorelogo.jpg" 
          alt="Marvel Store Logo" 
          style={{ width: "120px", marginBottom: "15px" }} 
        />

        <h2 className="text-2xl font-bold mb-4">🔐 Connexion</h2>

        {error && <p style={{ color: "red" }}>⚠️ {error}</p>}

        <form onSubmit={handleSubmit} className="flex flex-col">
          {/* 📨 Champ Email */}
          <div className="flex items-center bg-white text-black p-3 rounded mb-3">
            <Icon icon="mdi:email" width="24" className="mr-2" />
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="w-full bg-transparent focus:outline-none"
              required
            />
          </div>

          {/* 🔑 Champ Mot de passe */}
          <div className="flex items-center bg-white text-black p-3 rounded mb-3">
            <Icon icon="mdi:lock" width="24" className="mr-2" />
            <input
              type="password"
              placeholder="Mot de passe"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="w-full bg-transparent focus:outline-none"
              required
            />
          </div>

          {/* 🚀 Bouton Connexion */}
          <button
            type="submit"
            disabled={loading}
            className="bg-[#3E2F21] hover:bg-[#2D2116] text-white font-bold py-2 px-4 rounded mt-2 flex items-center justify-center"
          >
            {loading ? (
              <>
                <Icon icon="mdi:loading" width="20" className="mr-2 animate-spin" />
                Connexion...
              </>
            ) : (
              <>
                <Icon icon="mdi:login" width="20" className="mr-2" />
                Se connecter
              </>
            )}
          </button>
        </form>

        {/* 🔹 Liens Mot de passe oublié / Inscription */}
        <div className="text-center mt-4">
          <Link to="/forgot-password" className="text-[#3E2F21] hover:underline">
            <Icon icon="mdi:key" width="20" className="mr-1" />
            Mot de passe oublié ?
          </Link>
        </div>

        <p className="mt-3">
          Pas encore de compte ?{" "}
          <Link to="/register" className="text-[#3E2F21] hover:underline">
            <Icon icon="mdi:account-plus" width="20" className="mr-1" />
            Créer un compte
          </Link>
        </p>
      </div>
    </div>
  );
};

export default LoginPage;
