import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [cart, setCart] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/api/products")
      .then((response) => setProducts(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  // ðŸ” Filtrer les produits selon la recherche
  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // ðŸ›’ Ajouter un produit au panier
  const addToCart = (product) => {
    setCart([...cart, product]);
    alert(`${product.name} a Ã©tÃ© ajoutÃ© au panier !`);
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      {/* âœ… Barre de recherche */}
      <div className="flex justify-between items-center mb-6">
        <input
          type="text"
          placeholder="Rechercher un produit..."
          className="p-3 w-1/2 border rounded-lg"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <Link to="/cart" className="bg-blue-600 text-white p-3 rounded-lg">
          ðŸ›’ Voir le Panier
        </Link>
      </div>

      <h2 className="text-3xl font-bold text-center mb-6">Nos Produits</h2>

      {/* âœ… Liste des produits */}
      <div className="grid grid-cols-3 gap-6">
        {filteredProducts.map((product) => (
          <div key={product._id} className="bg-white p-6 rounded-lg shadow-md text-center">
            <h3 className="text-xl font-bold mb-2">{product.name}</h3>
            <p className="text-lg text-gray-700 mb-2">Prix : {product.price}â‚¬</p>
            {product.image && <img src={product.image} alt={product.name} className="w-40 mx-auto mb-4" />}
            <button 
              onClick={() => addToCart(product)} 
              className="bg-green-500 text-white p-3 rounded-lg hover:bg-green-600">
              âž• Ajouter au Panier
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductsPage;
